import ProfilePic from "../images/profile_pic.png";

const About = () => (
  <div className="about">
    <div className="about--img">
      <a className="img-link" href="https://github.com/wc356" target="_blank">
        <div className="img--container">
          <img src={ProfilePic} alt="profile picture" />
        </div>
      </a>
    </div>
    <div className="about--description"></div>
    <style jsx>
      {`
        .about {
          padding: 200px;
        }

        .about--img {
          align-items: center;
          display: flex;
          justify-content: center;
          width: 50%;
          overflow: hidden;
        }

        .img-link {
          position: relative;
          visibility: visible;
        }

        .img--container {
          display: block;
          overflow: hidden;
          position: relative;
          height: 400px;
          width: 400px;
          border: 3px solid blue;
        }

        img {
          align-items: center;
          position: absolute;
          object-fit: cover;
          object-position: 50% 50%;
          max-width: 90%;
          width: 450px;
          height: 100%;
        }

        .about--description {
          width: 50%;
        }
      `}
    </style>
  </div>
);

export default About;
