import React from "react";
import { useRouter } from "next/router";

import theme from "../../styles/theme";
import Layout from "../../components/Layout";
import projects from "../../database/projects";
import { v4 as uuidv4 } from "uuid";

export default () => {
  const router = useRouter();
  const title = router.query.project;

  const handleBody = () => {
    const body = projects.map(proj => {
      if (proj.project === title) {
        return (
          <div key={uuidv4()}>
            <div className="img">
              <img src={proj.picPath} height="350" width="600" />
            </div>
            <div className="body">
              <section className="overview">
                <h1>{Object.keys(proj.body[0])}</h1>
                <p className="indent">{Object.values(proj.body[0])}</p>
              </section>
              <section className="approach">
                <h1>{Object.keys(proj.body[1])}</h1>
                <ol className="indent" type="1">
                  <li>{proj.body[1]["approach"][0]}</li>
                  <li>{proj.body[1]["approach"][1]}</li>
                  <li>{proj.body[1]["approach"][2]}</li>
                  <li>{proj.body[1]["approach"][3]}</li>
                  <li>{proj.body[1]["approach"][4]}</li>
                </ol>
              </section>
              <section className="challenges">
                <h1>{Object.keys(proj.body[2])}</h1>
                <ul className="indent">
                  <li>{proj.body[2]["challenges"][0]}</li>
                  <li>{proj.body[2]["challenges"][1]}</li>
                  <li>{proj.body[2]["challenges"][2]}</li>
                  <li>{proj.body[2]["challenges"][3]}</li>
                </ul>
              </section>
              <section className="reflections">
                <h1>{Object.keys(proj.body[3])}</h1>
                <p className="indent">{proj.body[3]["reflections"][0]}</p>
                <p className="indent">{proj.body[3]["reflections"][1]}</p>
                <p className="indent">{proj.body[3]["reflections"][2]}</p>
              </section>
              <p className="body"></p>
            </div>
            <a
              onClick={() => {
                window.history.back();
              }}
              className="navlink"
            >
              BACK
            </a>

            <style jsx>
              {`
                .img {
                  display: block;
                }
                .body {
                  white-space: pre-wrap;
                  overflow-wrap: break-word;
                  margin: 40px 0;
                }
                .navlink {
                  display: inline-block;
                  cursor: pointer;
                  padding: 10px;
                  font-size: 15px;
                  font-weight: 900;
                  text-decoration: none;
                  color: white;
                  background-color: ${theme.colors.link};
                  border-radius: 10px;
                  letter-spacing: 2px;
                }
                .navlink:hover {
                  opacity: 0.5;
                }
                .navlink:visited {
                  color: white;
                }
                span {
                  font-size: 30px;
                  font-weight: 400;
                  margin-right: 5px;
                }
                li {
                  list-style: initial;
                  margin-left: 20px;
                }
                .indent {
                  padding: 10px;
                  padding-left: 30px;
                }
              `}
            </style>
          </div>
        );
      }
    });
    return body;
  };

  return (
    <Layout>
      <div className="layout">
        <div className="body">
          <h1 className="title">{title}</h1>
          {handleBody()}
        </div>
      </div>

      <style jsx>{`
        h1 {
          font-size: 34px;
          font-weight: 600;
          padding: 10px;
        }
        .layout {
          padding: 50px 225px;
          line-height: 1.5;
          background-color: rgb(239, 239, 239);
          min-height: 100vh;
        }
        .body {
          background: white;
          padding: 60px;
          height: 100%;
          border-radius: 10px;
          box-shadow: LightGray 0px 0px 10px 0px;
        }
        .title {
          display: block;
        }
      `}</style>
    </Layout>
  );
};
