import Link from "next/link";
import Layout from "../components/Layout";
import { blogContent } from "../data_storage/database";

export default () => {
  // Add Post
  const addPosts = () => {
    const posts = blogContent.map(post => (
      <li className="post" key={post.id}>
        <PostLink post={post} />
        <style jsx>
          {`
            .post {
              display: flex-box;
              background-color: white;
              width: 100%;
              padding: 20px;
              margin-bottom: 10px;
              box-shadow: LightGray 0px 0px 10px 0px;
              width: 100%;
              border-radius: 10px;
              height: 100px;
            }
          `}
        </style>
      </li>
    ));
    return posts;
  };

  // PostLink Component
  const PostLink = ({ post }) => (
    <>
      <Link href="/p/[title]" as={`/p/${post.title}`}>
        <a>{post.title} </a>
      </Link>
      <style jsx>
        {`
          a {
            font-weight: 600;
            text-decoration: none;
          }
          a:visited {
            color: Black;
          }
        `}
      </style>
    </>
  );

  return (
    <Layout>
      <div className="layout">
        <h1>Blog</h1>
        <section>
          <div className="card">
            <ul>{addPosts()}</ul>
          </div>
          <div className="card post">
            <p>Hello</p>
          </div>
        </section>
      </div>
      <style jsx>
        {`
          h1 {
            color: black;
            font-size: 60px;
            font-weight: 400;
            margin-bottom: 20px;
          }
          .layout {
            padding-top: 50px;
            padding-left: 120px;
            padding-right: 120px;
            line-height: 1.5;
            background-color: rgb(239, 239, 239);
            height: 100vh;
          }
          .card {
            display: inline-block;
            position: relative;
            top: 0px;
            height: 100%;
            width: 50%;
            border: solid 1px;
          }
          .post {
            background-color: white;
            padding: 20px;
            box-shadow: LightGray 0px 0px 10px 0px;
            border-radius: 20px;
          }
        `}
      </style>
    </Layout>
  );
};
