// import Markdown from "react-markdown";
import Layout from "../../components/Layout";
import { useRouter } from "next/router";
import { blogContent } from "../../data_storage/database";
import Link from "next/link";

export default () => {
  const router = useRouter();

  const handleBody = () => {
    const title = router.query.title;
    const body = blogContent.map(post => {
      if (post.title === title) {
        return (
          <>
            <div className="img">
              <img src={post.picPath} height="350" width="600" />
            </div>
            <p key={post.id}>{post.body}</p>
            <Link href="/blog">
              <a className="navlink">
                <span>â€¹</span> b a c k
              </a>
            </Link>
            <style jsx>
              {`
                .img {
                  display: block;
                }
                p {
                  white-space: pre-wrap;
                  overflow-wrap: break-word;
                  margin: 40px 0;
                }
                .navlink {
                  padding: 10px;
                  font-size: 15px;
                  font-weight: 900;
                  text-decoration: none;
                  color: white;
                  background-color: #8a2be2;
                  border-radius: 10px;
                }
                .navlink:hover {
                  opacity: 0.5;
                }
                .navlink:visited {
                  color: white;
                }
                span {
                  font-size: 30px;
                  font-weight: 400;
                  margin-right: 5px;
                }
              `}
            </style>
          </>
        );
      }
    });
    return body;
  };

  return (
    <Layout>
      <div className="layout">
        <div className="body">
          <h1>{router.query.title}</h1>
          {handleBody()}
        </div>
      </div>
      <style jsx>{`
        h1 {
          font-size: 34px;
          font-weight: 600;
          padding: 10px;
        }
        .layout {
          padding: 50px 225px;
          line-height: 1.5;
          background-color: rgb(239, 239, 239);
          min-height: 100vh;
        }
        .body {
          background: white;
          padding: 60px;
          height: 100%;
          border-radius: 10px;
          box-shadow: LightGray 0px 0px 10px 0px;
        }
      `}</style>
    </Layout>
  );
};
