import React, { useState, useEffect } from "react";
import theme from "../styles/theme";

import { otherProjects } from "../database/projects";

const OtherProject = ({ project }) => {
  const [link, setLink] = useState("");

  return (
    <a
      className="card"
      href={link}
      target="_blank"
      rel="nofollow noopener noreferrer"
    >
      <h3>{project}</h3>
      <div className="overlay"></div>
      <div className="body">
        <ul className="list">
          {otherProjects.map((proj) => {
            if (proj.project === project) {
              useEffect(() => {
                setLink(proj.link);
              }, [link]);
              return proj.description.map((item) => (
                <li className="list-item">{item}</li>
              ));
            }
          })}
        </ul>
      </div>

      <style jsx>
        {`
          a {
            text-decoration: none;
          }
          .card {
            display: block;
            width: 180px;
            height: 250px;
            padding: 18px;
            border: solid 1px ${theme.colors.gray};
            margin-right: 15px;
            margin-bottom: 15px;
            border-radius: 5px;
            background-color: ${theme.colors.dark};
            color: white;
          }
          .card:hover {
            color: ${theme.colors.dark};
            background-color: white;
            transform: translateY(-6px);
          }
          .card:hover .list-item {
            color: white;
            background-color: ${theme.colors.dark};
            transform: translateY(-6px);
            box-shadow: 6px 6px ${theme.colors.gray};
          }
          .card:hover .overlay {
            opacity: 1;
          }
          .overlay {
            opacity: 0;
          }
          .svg-wrapper {
            position: absolute;
            width: 50%;
            top: 50%;
            left: 50%;
            -webkit-transform: translate(-50%, -50%);
            -ms-transform: translate(-50%, -50%);
            transform: translate(-50%, -50%);
            fill: ${theme.colors.gray};
          }
          .text {
            color: ${theme.colors.link};
            font-size: 30px;
            font-weight: 500;
            position: absolute;
            top: 50%;
            left: 50%;
            letter-spacing: 1px;
            -webkit-transform: translate(-50%, -50%);
            -ms-transform: translate(-50%, -50%);
            transform: translate(-50%, -50%);
            text-align: center;
          }
          .body {
            display: flex;
            height: 100%;
            align-items: flex-end;
            text-align: right;
            padding-bottom: 18px;
          }
          .list {
            display: flex-box;
            width: 100%;
            transition: all 0.15s;
          }
          .list-item {
            display: inline-block;
            background-color: white;
            box-shadow: 6px 6px rgb(255, 153, 204);
            border-radius: 0 10px 0 10px;
            color: ${theme.colors.dark};
            font-weight: 400;
            margin-right: 10px;
            margin-bottom: 10px;
            padding: 5px;
          }
        `}
      </style>
    </a>
  );
};

export default OtherProject;
