import Header from "./Header";

const Layout = props => (
  <div>
    <Header className="header" />
    <div className="layoutStyle">{props.children}</div>
    <style jsx>
      {`
        .header {
          z-index: 3;
          pointer-events: all;
        }

        .layoutStyle {
          position: absolute;
          top: 0;
          width: 100%;
          height: 100%;
          box-sizing: border-box;
          border: 3px solid red;
        }
      `}
    </style>
  </div>
);

export default Layout;
