import Link from "next/link";
import ExpensifyPic from "../images/expensify.png";

const PostLink = ({ id = "PROJECT TITLE" }) => (
  <>
    <Link href="/p/[id]" as={`/p/${id}`}>
      <a className="title">{id}</a>
    </Link>
    <style jsx>
      {`
        .title {
          font-size: 20px;
          font-weight: 600;
          margin-bottom: 20px;
          text-decoration: none;
        }
        a:visited {
          color: black;
        }
      `}
    </style>
  </>
);

const PostImg = ({ id }) => (
  <>
    <Link href="/p/[id]" as={`/p/${id}`}>
      <div className="img--wrapper">
        <img src={ExpensifyPic} alt="picture of project Expensify" />
      </div>
    </Link>
    <style jsx>
      {`
        .img--wrapper {
          display: block;
          position: relative;
          align-items: center;
          text-align: center;
        }
        img {
          width: 100%;
          object-fit: cover;
          height: 348px;
          object-position: 50% 50%;
        }
      `}
    </style>
  </>
);

const Card = ({ id, quote = "QUOTE", description = "DESCRIPTION HERE" }) => {
  const renderTechStack = () => {
    let techstack = [
      "react",
      "redux",
      "reactrouter",
      "webpack",
      "babel",
      "jest",
      "enzyme",
      "firebase",
      "heroku",
      "git",
      "authentication"
    ];
    return (
      <ul>
        {techstack.map((val, i) => {
          return <li key={i}>{val}</li>;
        })}
        <style jsx>
          {`
            ul {
              display: flex;
              flex-direction: row;
              flex-wrap: wrap;
            }
            li {
              display: block;
              padding: 5px;
              margin-right: 10px;
              margin-bottom: 10px;
              border: 1px solid;
              border-radius: 9px;
            }
          `}
        </style>
      </ul>
    );
  };

  return (
    <li className="wrapper">
      <div className="card">
        <PostImg id={id} />
      </div>
      <div className="card card--desc">
        <div className="text">
          <PostLink id={id} />
          <h1>{quote}</h1>
          <p>{description}</p>
          <div className="tech-wrapper">{renderTechStack()}</div>
        </div>
      </div>
      <style jsx>
        {`
          .wrapper {
            display: flex;
            padding: 25px 0px;
          }
          .card {
            display: flex;
            height: 350px;
            overflow: hidden;
            width: 50%;
            box-shadow: 5px 5px 10px #999;
            border: 1px solid;
          }
          .card--desc {
            border: none;
            box-shadow: 5px 5px 10px #999;
          }
          .text {
            display: flex-box;
            font-family: Montserrat;
            padding: 30px 40px;
            text-align: left;
            white-space: pre-line;
            height: 100%;
          }
          h1 {
            margin-bottom: 30px;
          }
          .tech-wrapper {
            margin-top: 30px;
          }
        `}
      </style>
    </li>
  );
};

const Projects = () => (
  <section className="main">
    <div>
      <h1>Selected Projects.</h1>
    </div>
    <ul>
      <Card
        id="Expensify"
        quote="Quickly sort & track your expenses"
        description={`Full-stack web application built through guided tutorial on Udemy.`}
      />
      <Card />
      <Card />
      <Card />
    </ul>
    <style jsx>
      {`
        .main {
          display: flex;
          flex-direction: column;
          margin: 0 120px;
          text-align: center;
        }
        h1 {
          font-family: Montserrat;
          font-size: 45px;
          text-align: left;
          margin-bottom: 15px;
        }
      `}
    </style>
  </section>
);

export default Projects;
