// import Markdown from "react-markdown";
import Layout from "../../components/Layout";
import { useRouter } from "next/router";
import { blogContent } from "../../data_storage/database";
import Link from "next/link";

export default () => {
  const router = useRouter();

  const handleBody = () => {
    const title = router.query.title;
    const body = blogContent.map(post => {
      if (post.title === title) {
        return (
          <>
            <p key={post.id}>{post.body}</p>
            <Link href="/blog">
              <a className="navlink">
                <div className="navlink--inline">
                  <span>â€¹</span> b a c k
                </div>
              </a>
            </Link>
            <style jsx>
              {`
                p {
                  white-space: pre-wrap;
                  overflow-wrap: break-word;
                  margin-bottom: 40px;
                }
                .navlink {
                  padding: 10px;
                  font-size: 15px;
                  font-weight: 900;
                  text-decoration: none;
                  color: white;
                  background-color: black;
                }
                .navlink--inline {
                  display: inline;
                }
                .navlink:hover {
                  opacity: 0.5;
                }
                .navlink:visited {
                  color: white;
                }
                span {
                  font-size: 30px;
                  font-weight: 400;
                  margin-right: 5px;
                }
              `}
            </style>
          </>
        );
      }
    });
    return body;
  };

  return (
    <Layout>
      <div className="layout">
        <div className="body">
          <h1>{router.query.title}</h1>
          {handleBody()}
        </div>
      </div>
      <style jsx>{`
        h1 {
          font-size: 40px;
          font-weight: 400;
          margin-bottom: 50px;
        }
        .layout {
          padding: 50px 230px;
          line-height: 1.5;
          background-color: rgb(239, 239, 239);
          min-height: 100vh;
        }
        .body {
          background: white;
          padding: 60px;
          height: 100%;
          border-radius: 5px;
          box-shadow: LightGray 0px 0px 10px 0px;
        }
      `}</style>
    </Layout>
  );
};
