import Link from "next/link";
import SocialLinks from "./SocialLinks";

const Header = () => (
  <>
    <div className="wrapper">
      <Link href="/">
        <a className="navlink">\ H O M E \</a>
      </Link>
      <Link href="/about">
        <a className="navlink">— A B O U T —</a>
      </Link>
    </div>
    <SocialLinks className="social" />
    <style jsx>
      {`
        .wrapper {
          display: flex;
          flex-direction: column;
          height: 100px;
          justify-content: center;
          margin-left: 15px;
          position: fixed;
          text-align: center;
          top: 45vh;
          z-index: 1;
        }

        .navlink {
          color: #3366cc;
          font-family: "Montserrat";
          font-size: 13px;
          font-weight: 900;
          text-decoration: none;
          margin: 70px 0;
          transform: rotate(-90deg);
        }

        .navlink:hover {
          opacity: 0.5;
        }
      `}
    </style>
  </>
);

export default Header;
