import ProfilePic from "../images/profile_pic.png";

const About = () => (
  <div className="about">
    <section className="about--img">
      <a className="img-link" href="https://github.com/wc356" target="_blank">
        <div className="img--container">
          <img src={ProfilePic} alt="profile picture" />
        </div>
      </a>
    </section>
    <section className="about--description">
      <div className="description--wrapper">
        <h1>I am..</h1>
        <p>a Designer, and a Front-End Web Developer.</p>
        <p>appreciative of Simple, Yet, Elegant designs.</p>
        <p>always Learning.</p>
      </div>
    </section>
    <style jsx>
      {`
        .about {
          margin: 200px 0;
          display: flex;
          justify-content: space-between;
        }
        .about--img {
          align-items: center;
          display: flex;
          justify-content: center;
          overflow: hidden;
          width: 50%;
        }
        .img--container {
          text-align: center;
          display: block;
          overflow: hidden;
          position: relative;
          height: 350px;
          width: 400px;
          margin-left: 50px;
        }
        img {
          object-fit: cover;
          object-position: 50% 50%;
          max-width: 90%;
          width: 450px;
          height: 100%;
        }
        .about--description {
          width: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        h1 {
          color: #3366cc;
        }

        .description--wrapper {
          font-family: "Futura";
          text-align: center;
          font-size: 18px;
        }
      `}
    </style>
  </div>
);

export default About;
