import Header from "./Header";
import SocialLinks from "./SocialLinks";

const Layout = props => (
  <div>
    <Header />
    <div className="layout">{props.children}</div>
    <SocialLinks className="social" />
    <style jsx>
      {`
        *,
        *::before,
        *::after {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }

        .layout {
          height: 100%;
          pointer-events: none;
          position: absolute;
          top: 0;
          width: 100%;
        }

        .social {
          border: 3px pink solid;
          display: absolute;
          position: fixed;
          bottom: 30px;
          z-index: 1;
        }
      `}
    </style>
  </div>
);

export default Layout;
