import Layout from "../../components/Layout";
import blog from "../../database/blog";
import { useRouter } from "next/router";
import Link from "next/link";
import { v4 as uuidv4 } from "uuid";
import theme from "../../styles/theme";

export default () => {
  const router = useRouter();
  const title = router.query.title;

  const handleBody = () => {
    const body = blog.map((post) => {
      if (post.title === title) {
        return (
          <div className="body" key={uuidv4()}>
            <div className="img">
              <img src={post.picPath} height="350" width="600" />
            </div>
            <p className="body" key={post.id}>
              {post.body}
            </p>
            <div className="align-left">
              <Link href="/blog">
                <a className="navlink">BACK</a>
              </Link>
            </div>

            <style jsx>
              {`
                .body {
                  display: flex;
                  flex-direction: column;
                  align-items: center;
                }
                .img {
                  display: block;
                }
                .body {
                  white-space: pre-wrap;
                  overflow-wrap: break-word;
                  margin: 40px 0;
                }
                .align-left {
                  display: flex;
                  text-align: center;
                  flex-direction: column;
                  width: 30%;
                }
                .navlink {
                  display: block;
                  letter-spacing: 1px;
                  padding: 10px;
                  font-size: 15px;
                  font-weight: 900;
                  text-decoration: none;
                  color: white;
                  background-color: ${theme.colors.dark};
                  border-radius: 10px;
                }
                .navlink:hover {
                  background-color: ${theme.colors.pink};
                  transform: scale(1.08);
                }
                .navlink:visited {
                  color: white;
                }
                span {
                  font-size: 30px;
                  font-weight: 400;
                  margin-right: 5px;
                }
              `}
            </style>
          </div>
        );
      }
    });
    return body;
  };

  return (
    <Layout>
      <div className="layout">
        <div className="body">
          <h1>{title}</h1>
          {handleBody()}
        </div>
      </div>
      <style jsx>{`
        h1 {
          font-size: 34px;
          font-weight: 600;
          padding: 10px;
          text-align: center;
        }
        .layout {
          padding: 50px 210px;
          line-height: 1.8;
          background-color: rgb(239, 239, 239);
          min-height: 100vh;
          background-image: linear-gradient(
            0deg,
            ${theme.colors.dark} 0%,
            ${theme.colors["pink-link"]} 80%,
            white 100%
          );
        }
        .body {
          background: white;
          color: ${theme.colors.black};
          padding: 60px 100px;
          height: 100%;
          border-radius: 10px;
          box-shadow: LightGray 0px 0px 10px 0px;
          white-space: nowrap;
        }
      `}</style>
    </Layout>
  );
};
