import Link from "next/link";
import ExpensifyPic from "../images/expensify.png";

const PostLink = ({ id = "PROJECT TITLE" }) => (
  <>
    <Link href="/p/[id]" as={`/p/${id}`}>
      <a className="title">{id}</a>
    </Link>
    <style jsx>
      {`
        .title {
          font-size: 20px;
          font-weight: 600;
          margin-bottom: 20px;
          text-decoration: none;
        }
      `}
    </style>
  </>
);

const Card = ({ id, description = "DESCRIPTION HERE" }) => (
  <li className="wrapper">
    <div className="card">
      <a>
        <div className="img--wrapper">
          <img src={ExpensifyPic} alt="picture of project Expensify" />
        </div>
      </a>
    </div>
    <div className="card card--desc">
      <div className="text">
        <PostLink id={id} />
        <p>{description}</p>
      </div>
    </div>
    <style jsx>
      {`
        .wrapper {
          display: flex;
          padding: 25px 0px;
        }
        .card {
          border: 1px solid;
          display: flex;
          height: 350px;
          overflow: hidden;
          width: 50%;
        }
        .card--desc {
          border: none;
          box-shadow: 5px 5px 10px #999;
        }
        .text {
          display: block;
          font-family: Montserrat;
          padding: 30px 40px;
          text-align: left;
        }
        .img--wrapper {
          display: block;
          position: relative;
          align-items: center;
          text-align: center;
        }
        img {
          position: relative;
          max-width: 100%;
          object-fit: cover;
          object-position: 50% 50%;
        }
      `}
    </style>
  </li>
);

const Projects = () => (
  <section className="main">
    <div>
      <h1>Selected Projects.</h1>
    </div>
    <ul>
      <Card
        id="Expensify"
        description="Fullstack web application made using ReactJS, Firebase, Redux."
      />
      <Card />
      <Card />
      <Card />
    </ul>
    <style jsx>
      {`
        .main {
          display: flex;
          flex-direction: column;
          margin: 0 120px;
          text-align: center;
        }
        h1 {
          font-family: Montserrat;
          font-size: 45px;
          text-align: left;
          margin-bottom: 15px;
        }
      `}
    </style>
  </section>
);

export default Projects;
