import Link from "next/link";

const PostLink = ({ title = "PROJECT TITLE" }) => (
  <li>
    <Link href={`/post?title=${title}`}>
      <a className="title">{title}</a>
    </Link>
    <style jsx>
      {`
        .title {
          font-size: 20px;
          font-weight: 600;
          margin-bottom: 20px;
          text-decoration: none;
        }
      `}
    </style>
  </li>
);

const Card = ({ title, description = "DESCRIPTION HERE" }) => (
  <section className="wrapper">
    <div className="card"></div>
    <div className="card card--desc">
      <div className="text">
        <PostLink title={title} />
        <p>{description}</p>
      </div>
    </div>
    <style jsx>
      {`
        .wrapper {
          display: flex;
          padding: 25px 0px;
        }
        .card {
          border: 1px solid;
          display: flex;
          height: 350px;
          width: 50%;
        }
        .card--desc {
          border: none;
          box-shadow: 5px 5px 10px #999;
        }
        .text {
          display: block;
          font-family: Montserrat;
          padding: 30px 40px;
          text-align: left;
        }
      `}
    </style>
  </section>
);

const Projects = () => (
  <section className="main">
    <div>
      <h1>Selected Projects.</h1>
    </div>
    <ul>
      <Card
        title="Expensify"
        description="Fullstack web application made using ReactJS, Firebase, Redux."
      />
      <Card />
      <Card />
      <Card />
    </ul>
    <style jsx>
      {`
        .main {
          display: flex;
          flex-direction: column;
          margin: 0 120px;
          text-align: center;
        }
        h1 {
          font-family: Montserrat;
          font-size: 45px;
          text-align: left;
          margin-bottom: 15px;
        }
      `}
    </style>
  </section>
);

export default Projects;
