import Link from "next/link";

const Landing = () => (
  <div className="landing">
    <section id="intro">
      <a href="">
        <h1>WOO YOUNG CHOI</h1>
      </a>
      <p>
        <span>Designer</span> & <span>Front-end Developer</span> based in NY.
      </p>
    </section>
    <style jsx>
      {`
        .landing {
          align-items: center;
          display: flex;
          flex-direction: column;
          font-family: Futura;
          justify-content: center;
          min-height: 100vh;
          text-align: center;
        }
        a {
          text-decoration: none;
        }
        h1 {
          font-family: Futura;
          font-size: 70px;
          font-weight: 600;
        }

        h1:hover {
          text-shadow: 10px 10px #ccc;
        }

        p {
          font-family: Montserrat;
          font-size: 16px;
          font-weight: 100;
        }

        span {
          font-size: 19px;
          font-weight: 900;
        }
      `}
    </style>
  </div>
);

export default Landing;
