import ProfilePic from "../images/profile_pic.png";

const About = () => (
  <div className="about">
    <section className="about--img">
      <a className="img-link" href="https://github.com/wc356" target="_blank">
        <div className="img--container">
          <img src={ProfilePic} alt="profile picture" />
        </div>
      </a>
    </section>
    <section className="about--description">
      <div className="description--wrapper">
        <h1>01. About</h1>
        <h3>HEADLINE HERE</h3>
        <p>Designer turned front-end web developer residing in New York.</p>
        <p>Transforming ideas into accessible and meaningful services.</p>
        <p>Simple, yet, effective designs.</p>
      </div>
    </section>
    <style jsx>
      {`
        .about {
          margin: 200px 0;
          display: flex;
          justify-content: space-between;
        }
        .about--img {
          align-items: center;
          display: flex;
          justify-content: center;
          overflow: hidden;
          width: 50%;
        }
        .img--container {
          text-align: center;
          display: block;
          overflow: hidden;
          position: relative;
          height: 400px;
          min-width: 400px;
          margin-left: 130px;
        }
        .img-link {
          border: 1px solid;
        }
        img {
          object-fit: cover;
          object-position: 50% 50%;
          max-width: 75%;
          width: 450px;
          height: 100%;
        }
        .about--description {
          width: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .description--wrapper {
          font-size: 18px;
          padding: 120px;
        }
        h1 {
          text-align: center;
        }
        h3 {
          font-family: Montserrat;
          font-size: 20px;
          text-align: center;
        }
        p {
          font-family: Montserrat;
          font-size: 16px;
        }
      `}
    </style>
  </div>
);

export default About;
