import Link from "next/link";

const PostLink = props => (
  <li>
    <Link href={`/post?title=${props.title}`}>
      <a>{props.title}</a>
    </Link>
  </li>
);

const Card = () => (
  <div className="card-wrapper">
    <div className="card"></div>
    <div className="card card--desc">
      <div className="desc">
        <PostLink title="Expensify" />
        <p>Fullstack web application made using ReactJS, Firebase, Redux</p>
      </div>
    </div>
    <style jsx>
      {`
        .card-wrapper {
          display: flex;
          padding: 25px 0px;
        }
        .card {
          border: 1px solid;
          display: flex;
          height: 350px;
          width: 50%;
        }
        .card--desc {
          border: none;
          box-shadow: 5px 5px 10px #999;
        }
        .desc {
          display: block;
          font-family: Montserrat;
          padding: 30px 40px;
          text-align: left;
        }
        .desc--title {
          font-weight: 600;
          margin-bottom: 20px;
        }
      `}
    </style>
  </div>
);

const Projects = () => (
  <div className="main">
    <div>
      <h1>Selected Projects.</h1>
    </div>
    <ul>
      <Card />
      <Card />
      <Card />
      <Card />
    </ul>
    <style jsx>
      {`
        .main {
          display: flex;
          flex-direction: column;
          margin: 0 120px;
          text-align: center;
        }
        h1 {
          font-family: Montserrat;
          font-size: 45px;
          text-align: left;
          margin-bottom: 15px;
        }
        .card-wrapper {
          display: flex;
          padding: 25px 0px;
        }
        .card {
          border: 1px solid;
          display: flex;
          height: 350px;
          width: 50%;
        }
        .card--desc {
          border: none;
          box-shadow: 5px 5px 10px #999;
        }
        .desc {
          display: block;
          font-family: Montserrat;
          padding: 30px 40px;
          text-align: left;
        }
        .desc--title {
          font-weight: 600;
          margin-bottom: 20px;
        }
      `}
    </style>
  </div>
);

export default Projects;
