import Link from "next/link";
import ExpensifyPic from "../images/expensify.png";

const PostLink = ({ id = "PROJECT TITLE" }) => (
  <>
    <Link href="/p/[id]" as={`/p/${id}`}>
      <a className="title">{id}</a>
    </Link>
    <style jsx>
      {`
        .title {
          font-size: 20px;
          font-weight: 600;
          margin-bottom: 20px;
          text-decoration: none;
          color: #55a5e7;
        }
        a:visited {
          color: #55a5e7;
        }
      `}
    </style>
  </>
);

const PostImg = ({ id }) => (
  <>
    <Link href="/p/[id]" as={`/p/${id}`}>
      <div className="img--wrapper">
        <img src={ExpensifyPic} alt="picture of project Expensify" />
      </div>
    </Link>
    <style jsx>
      {`
        .img--wrapper {
          display: block;
          position: relative;
          align-items: center;
          text-align: center;
        }
        img {
          width: 100%;
          object-fit: cover;
          height: 348px;
          object-position: 50% 50%;
        }
      `}
    </style>
  </>
);

const Card = ({ id, quote = "QUOTE", description = "DESCRIPTION HERE" }) => {
  const renderTechStack = () => {
    let techstack = [
      "react",
      "redux",
      "react-router",
      "webpack",
      "babel",
      "jest",
      "enzyme",
      "firebase",
      "heroku",
      "git",
      "scss / sass",
      "authentication"
    ];
    return (
      <ul>
        {techstack.map((val, i) => {
          return <li key={i}>{val}</li>;
        })}
        <style jsx>
          {`
            ul {
              display: flex;
              flex-direction: row;
              flex-wrap: wrap;
            }
            li {
              display: flexbox;
              padding: 8px;
              margin-right: 10px;
              margin-bottom: 10px;
              color: black;
              border: 3px solid #65b2d8;
              border-radius: 9px;
            }
          `}
        </style>
      </ul>
    );
  };

  return (
    <li className="wrapper">
      <div className="card">
        <PostImg id={id} />
      </div>
      <div className="card card--desc">
        <div className="text-wrapper">
          <div className="text">
            <PostLink id={id} />
            <h1>{quote}</h1>
            <p>{description}</p>
            <div className="tech-wrapper">{renderTechStack()}</div>
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .wrapper {
            display: flex;
            padding: 35px 0px;
          }
          .card {
            display: block;
            height: 350px;
            overflow: hidden;
            width: 50%;
            border: 1px solid gray;
          }
          .card--desc {
            border: none;
          }
          .text-wrapper {
            display: flex;
            align-items: center;
          }
          .text {
            font-family: Montserrat;
            padding: 0px 30px;
            white-space: pre-line;
            height: 100%;
          }
          h1 {
            margin-bottom: 20px;
          }
          .tech-wrapper {
            margin-top: 20px;
          }
        `}
      </style>
    </li>
  );
};

const Projects = () => (
  <section className="main">
    <div>
      <h1>Selected Projects.</h1>
    </div>
    <ul>
      <Card
        id="Expensify"
        quote="Quickly sort & track your expenses"
        description={`Full-stack web application built through guided tutorial on Udemy.`}
      />
      <Card />
      <Card />
      <Card />
    </ul>
    <style jsx>
      {`
        .main {
          display: flex;
          flex-direction: column;
          margin: 0 120px;
          text-align: center;
        }
        h1 {
          font-family: Montserrat;
          font-size: 45px;
          margin-bottom: 15px;
        }
      `}
    </style>
  </section>
);

export default Projects;
