import ProfilePic from "../images/profile_pic.png";

const About = () => (
  <div className="about">
    <div className="about--img">
      <a className="img-link" href="https://github.com/wc356">
        <div className="img--container">
          <img src={ProfilePic} alt="profile picture" />
        </div>
      </a>
    </div>
    <div className="about--description"></div>
    <style jsx>
      {`
        .about {
          padding: 200px;
        }

        .about--img {
          width: 50%;
          display: block;
          overflow: hidden;
          border: 3px blue solid;
        }

        .img--container {
          display: block;
          overflow: hidden;
          position: relative;
          height: 400px;
          width: 600px;
        }

        img {
          position: absolute;
          vertical-align: middle;
          object-fit: cover;
          object-position: 50% 50%;
          max-width: 100%;
          width: 300px;
          height: 300px;
        }

        .about--description {
          width: 50%;
        }
      `}
    </style>
  </div>
);

export default About;
