import { PostLink, PostImg } from "../actions/Post";

const Card = ({ id, quote = "QUOTE", description = "DESCRIPTION HERE" }) => {
  const techstack = {
    expensify: [
      "react",
      "redux",
      "react-router",
      "webpack",
      "babel",
      "jest",
      "enzyme",
      "firebase",
      "heroku",
      "git",
      "scss / sass",
      "authentication"
    ],
    indecision: ["react", "babel", "sass", "git", "webpack"]
  };

  const renderTechstack = id => {
    if (techstack.hasOwnProperty(id)) {
      return (
        <ul>
          {techstack[id].map((val, i) => {
            return <li key={i}>{val}</li>;
          })}
          <style jsx>
            {`
              ul {
                display: flex;
                flex-direction: row;
                flex-wrap: wrap;
              }
              li {
                display: flexbox;
                padding: 8px;
                margin-right: 10px;
                margin-bottom: 10px;
                color: black;
                border: 3px solid #65b2d8;
                border-radius: 9px;
              }
            `}
          </style>
        </ul>
      );
    } else {
      console.log(techstack.hasOwnProperty(id));
    }
  };

  const projName = id;
  console.log(typeof id);

  return (
    <li className="wrapper">
      <div className="card">
        <PostImg id={id} />
      </div>
      <div className="card card--desc">
        <div className="text-wrapper">
          <div className="text">
            <PostLink id={id} />
            <h1>{quote}</h1>
            <p>{description}</p>
            <div className="tech-wrapper">{renderTechstack(projName)}</div>
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .wrapper {
            display: flex;
            padding: 35px 0px;
          }
          .card {
            display: block;
            height: 350px;
            overflow: hidden;
            width: 60%;
            border: 1px solid gray;
          }
          .card--desc {
            border: none;
            text-align: center;
          }
          .text-wrapper {
            align-items: center;
          }
          .text {
            padding: 0px 30px;
            white-space: pre-line;
            height: 100%;
          }
          h1 {
            margin-bottom: 20px;
            margin-top: 10px;
          }
          .tech-wrapper {
            margin-top: 20px;
          }
        `}
      </style>
    </li>
  );
};

const Projects = () => (
  <section className="main">
    <div>
      <h1>Selected Projects</h1>
    </div>
    <ul>
      <Card
        id="Expensify"
        quote="quickly Sort & Track your Expenses"
        description={`Full-stack web application built through guided tutorial on Udemy.`}
      />
      <Card />
      <Card />
      <Card />
    </ul>
    <style jsx>
      {`
        .main {
          display: flex;
          flex-direction: column;
          margin: 0 120px;
          text-align: center;
        }
        h1 {
          font-size: 45px;
          margin-bottom: 50px;
        }
      `}
    </style>
  </section>
);

export default Projects;
