import React from "react";
import { v4 as uuidv4 } from "uuid";

import resume from "../database/resume";

const Resume = () => {
  const values = Object.values(resume);

  const renderSectionName = () => (
    <div className="name">
      <h1>{values[0]}</h1>
    </div>
  );

  const renderSectionBtn = () => (
    <div className="btn-wrapper">
      <a className="btn-download" href={values[10][1]} target="_blank">
        {values[10][0]}
      </a>
    </div>
  );

  const renderExperience = (expType) => {
    const renderifLocationExist = (exp) =>
      exp.location && (
        <>
          , <span>{exp.location}</span>
          <style jsx>
            {`
              span {
                color: gray;
                font-weight: 500;
              }
            `}
          </style>
        </>
      );

    const renderGithubLink = (exp, expType) => {
      switch (expType) {
        case "professional":
          return "";
        case "project":
          return (
            <>
              -{" "}
              <a
                href={exp.link[1]}
                target="_blank"
                rel="nofollow noopener noreferrer"
              >
                {exp.link[0]}
              </a>
              <style jsx>
                {`
                  a {
                    display: inline-block;
                    font-size: 17px;
                    text-decoration: none;
                    position: relative;
                    transition: all 0.15s;
                  }
                  a::after {
                    position: absolute;
                    height: 2px;
                    width: 100%;
                    margin: 0 auto;
                    left: 0;
                    right: 0;
                    background: ${values[9].highlight};
                    content: "";
                    bottom: 0;
                    /* animation */
                    -o-transition: 0.15s;
                    -ms-transition: 0.15s;
                    -moz-transition: 0.15s;
                    -webkit-transition: 0.15s;
                    transition: 0.15s;
                  }
                  a:hover::after {
                    width: 20%;
                    background: red;
                  }
