import ProfilePic from "../images/profile_pic.png";

const About = () => (
  <div className="about">
    <section className="about--img">
      <a className="img-link" href="https://github.com/wc356" target="_blank">
        <div className="img--container">
          <img src={ProfilePic} alt="profile picture" />
        </div>
      </a>
    </section>
    <section className="about--description">
      <h1>About Me</h1>
      <p>Hello!</p>
    </section>
    <style jsx>
      {`
        .about {
          padding: 200px;
        }

        .about--img {
          align-items: center;
          display: flex;
          justify-content: center;
          width: 50%;
          overflow: hidden;
        }

        .img--container {
          display: block;
          overflow: hidden;
          position: relative;
          height: 400px;
          width: 400px;
        }

        img {
          object-fit: cover;
          object-position: 50% 50%;
          max-width: 90%;
          width: 450px;
          height: 100%;
        }

        .about--description {
          width: 50%;
        }
      `}
    </style>
  </div>
);

export default About;
