import Layout from "../components/Layout";
import Link from "next/link";

export default function Blog() {
  const blogContent = [
    {
      id: "self-taught-challenges",
      title: "Challenges of a Self-Taught Developer"
    },
    { id: "loops", title: "Looping through objects" },
    { id: "reflections", title: "Current Reflections" }
  ];

  // Add Post
  const getPosts = () => {
    blogContent.map(post => (
      <>
        <li className="post" key={post.id}>
          <PostLink key={post.id} post={post} />
        </li>
        <style jsx>
          {`
            .post {
              display: flex-box;
              width: 800px;
              background-color: white;
              padding: 40px;
              margin-bottom: 10px;
              box-shadow: LightGray 0px 0px 10px 0px;
            }
          `}
        </style>
      </>
    ));
  };

  // PostLink Component
  const PostLink = ({ post }) => (
    <>
      <Link href="/p/[title]" as={`/p/${post.title}`}>
        <a className="nav-link">{post.title}</a>
      </Link>
      <style jsx>
        {`
          .nav-link {
            text-decoration: none;
          }
        `}
      </style>
    </>
  );

  return (
    <Layout>
      <div className="layout">
        <h1>Tech_Blog</h1>
        <h2>Posts</h2>
        <section>
          <ul>{getPosts()}</ul>
        </section>
      </div>
      <style jsx>
        {`
          h1 {
            font-size: 50px;
            margin-bottom: 50px;
          }
          h2 {
            font-family: Montserrat;
            margin-bottom: 10px;
            font-size: 40px;
            font-weight: 400;
          }
          .layout {
            padding-top: 50px;
            padding-left: 120px;
            line-height: 1.5;
            background-color: rgb(239, 239, 239);
          }
          .nav-link {
            text-decoration: none;
          }
        `}
      </style>
    </Layout>
  );
}
