import ProfilePic from "../images/profile_pic.png";

const About = () => (
  <div className="main">
    <section className="img">
      <div className="img--wrapper">
        <img src={ProfilePic} alt="profile picture" />
      </div>
    </section>
    <section className="about">
      <div className="about--wrapper">
        <h1>Think, Design and Code</h1>
        <p>
          Woo Young Choi is an upcoming UI/UX Designer & Front-End Web Developer
          based in New York, NY. Throughout his studio-based design education in
          Cornell University, Woo embraced the sublime notion of “simplicity”
          and continues to explore its integration of two core web
          mediums—design and code. He has 1 year of professional experience
          working as a designer in Alexandria, VA and is a certified LEED Green
          Associate. Woo currently resides in Cliffside Park, NJ, where he
          devotes most of his time developing Full-stack Web Applications and
          Front-end Websites using ReactJS as his preferred method.
        </p>
      </div>
    </section>
    <style jsx>
      {`
        .main {
          margin: 300px 0px;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }
        .img {
          display: flex;
          justify-content: center;
          overflow: hidden;
          width: 50%;
        }
        .img--wrapper {
          text-align: center;
          display: block;
          overflow: hidden;
          position: relative;
          height: 400px;
          min-width: 400px;
        }
        img {
          object-fit: cover;
          object-position: 50% 50%;
          max-width: 75%;
          width: 450px;
          height: 100%;
        }

        .about {
          align-items: center;
          display: flex;
          justify-content: center;
          width: 50%;
        }
        .about--wrapper {
          font-size: 18px;
          padding: 120px;
        }
        h1 {
          font-size: 35px;
          text-align: center;
          font-weight: 400;
          font-family: Montserrat;
        }
        h3 {
          font-family: Montserrat;
          font-size: 20px;
          text-align: center;
        }
        p {
          line-height: 30px;
          font-family: Montserrat;
          font-size: 16px;
        }
      `}
    </style>
  </div>
);

export default About;
