import theme from "../styles/theme";

const Landing = () => (
  <div className="main">
    <h1 className="title">wooyoungchoi.dev</h1>
    <p className="description">UI/UX & Front-end Developer</p>
    <style jsx>
      {`
        .main {
          display: flex;
          align-items: center;
          flex-direction: column;
          justify-content: center;
          min-height: 100vh;
          text-align: center;
        }
        .title {
          font-family: Futura, Montserrat;
          font-size: 5px;
          font-weight: 300;
          margin-bottom: 25px;
          animation: text-drop 0.75s 1;
          letter-spacing: 8px;
        }
        .description {
          font-size: 27px;
          font-weight: 500;
          letter-spacing: 2px;
          color: ${theme.colors.black};
        }
        @keyframes text-drop {
          0% {
            opacity: 0;
            margin-bottom: -200px;
          }
        }
        @media only screen and (max-width: 1024px) {
          .main {
            flex-direction: column;
          }
          .description {
            font-size: 1em;
          }
        }
      `}
    </style>
  </div>
);

export default Landing;
