import Link from "next/link";

const Header = () => (
  <>
    <div className="navbar">
      <div className="wrapper">
        <Link href="/">
          <a className="navlink">Home</a>
        </Link>
        <Link href="/about">
          <a className="navlink">About</a>
        </Link>
      </div>
    </div>
    <style jsx>
      {`
        .navbar {
          display: block;
          width: 50px;
          height: 100vh;
          border: 3px solid yellow;
        }

        .wrapper {
          position: absolute;
          top: 50%;
          height: 100px;
        }

        .navlink {
          display: inline-block;
          transform: rotate(-90deg);
          color: #00ace6;
          font-family: "Arial";
          text-decoration: none;
          box-shadow: 3px 3px 10px 0.5px #ddd;
        }

        .navlink:hover {
          opacity: 0.6;
        }
      `}
    </style>
  </>
);

export default Header;
