import Link from "next/link";
import Layout from "../components/Layout";
import { blogContent } from "../data_storage/database";

export default () => {
  // Add Post
  const addPosts = () => {
    const posts = blogContent.map(post => (
      <li className="post" key={post.id}>
        <PostLink post={post} />
        <style jsx>
          {`
            .post {
              display: flex-box;
              background-color: white;
              padding: 20px;
              margin-bottom: 10px;
              box-shadow: LightGray 0px 0px 10px 0px;
              border-radius: 10px;
            }
          `}
        </style>
      </li>
    ));
    return posts;
  };

  // PostLink Component
  const PostLink = ({ post }) => (
    <>
      <Link href="/p/[title]" as={`/p/${post.title}`}>
        <a>{post.title} </a>
      </Link>
      <style jsx>
        {`
          a {
            font-weight: 600;
            text-decoration: none;
          }
          a:visited {
            color: Black;
          }
        `}
      </style>
    </>
  );

  return (
    <Layout>
      <div className="layout">
        <h1>Blog</h1>
        <div className="card-wrapper">
          <div className="card">
            <ul className="list">{addPosts()}</ul>
          </div>
          <div className="card">
            <div className="blog-content">
              <p>Hello</p>
            </div>
          </div>
        </div>
      </div>
      <style jsx>
        {`
          h1 {
            color: black;
            font-size: 60px;
            font-weight: 400;
            margin-bottom: 20px;
          }
          .layout {
            padding-top: 50px;
            padding-left: 120px;
            padding-right: 120px;
            background-color: rgb(239, 239, 239);
            height: 100vh;
          }
          .list {
            width: 100%;
          }
          .card-wrapper {
            display: block;
            height: 100vh;
          }
          .card {
            display: inline-flex;
            align-items: flex-start;
            height: 100%;
            width: 50%;
            border: dashed 1px;
            padding: 10px;
          }
          .blog-content {
            display: flex-box;
            height: 100%;
            background-color: white;
            padding: 20px;
            border-radius: 10px;
            box-shadow: LightGray 0px 0px 10px 0px;
          }
        `}
      </style>
    </Layout>
  );
};
