import ProfilePic from "../images/profile_pic.png";

const About = () => (
  <div id="about">
    <div id="picture">
      <a href="https://github.com/wc356">
        <div className="img--container">
          <img src={ProfilePic} alt="profile picture" />
        </div>
      </a>
    </div>
    <style jsx>
      {`
        #about {
          padding: 200px;
        }

        #picture {
          border: 3px blue solid;
          height: 400px;
          width: 500px;
        }

        .img--container {
          cursor: pointer;
          display: block;
          overflow-x: hidden;
          overflow-y: hidden;
          position: relative;
          height: 400px;
          width: 600px;
        }

        img {
          width: 100%;
          height: 100%;
        }
      `}
    </style>
  </div>
);

export default About;
