import Header from "./Header";

const Layout = props => (
  <div>
    <Header />
    <div className="layout">{props.children}</div>
    <style jsx global>
      {`
        *,
        *::before,
        *::after {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }

        .layout {
          height: 100%;
          pointer-events: none;
          position: absolute;
          top: 0;
          width: 100%;
        }
      `}
    </style>
  </div>
);

export default Layout;
