import Link from "next/link";

const logoStyle = {
  display: block,
  fontSize: 25,
  marginRight: 15
};

const linkStyle = {
  marginRight: 15
};

const Header = () => (
  <>
    <Link href="/">
      <a style={logoStyle}>WYC</a>
    </Link>
    <Link href="/">
      <a style={linkStyle}>Home</a>
    </Link>
    <Link href="/about">
      <a style={linkStyle}>About</a>
    </Link>
    <style jsx>
      {`
        a {
          text-decoration: none;
          font-family: "Arial";
        }

        a:hover {
          opacity: 0.6;
        }
      `}
    </style>
  </>
);

export default Header;
