import { useRouter } from "next/router";
import Layout from "../components/MyLayout";

// Page Content
const Content = () => {
  const router = useRouter();
  return (
    <>
      <h1>{router.query.title}</h1>
      <p>This is the blog post content.</p>
    </>
  );
};

// Page Template
const Page = () => (
  <Layout>
    <Content />
  </Layout>
);

export default Page;
