import { useRouter } from "next/router";
import Markdown from "react-markdown";
import Layout from "../../components/Layout";

export default () => {
  const router = useRouter();

  return (
    <Layout>
      <div className="layout">
        <h1>{router.query.title}</h1>
      </div>
      <style jsx>{`
        h1 {
          font-size: 40px;
          margin-bottom: 50px;
        }
        .layout {
          padding-top: 50px;
          padding-left: 100px;
        }
      `}</style>
    </Layout>
  );
};
