import React, { useEffect } from "react";
import resume from "../database/resume";

const Resume = ({
  name,
  contact,
  education,
  experience,
  technology,
  honors,
  languages,
  interests,
  hobbies,
}) => {
  console.log(contact);

  const renderExperience = (expType) => (
    <>
      <h3>{expType.toUpperCase()} EXPERIENCE</h3>
      {experience[expType].map((exp) => {
        const locationExist = exp.location ? `, ${exp.location}` : "";
        return (
          <div>
            <h2>
              {exp.title}
              <span>{locationExist}</span>
            </h2>
            <p>{exp.subTitle}</p>
            <ul>
              exp.description.map(desc => (<li>{desc}</li>
              ))
            </ul>
          </div>
        );
      })}
    </>
  );

  return (
    <div className="page-layout">
      <section className="HEAD">
        <div className="name">{name}</div>
        <div className="contact-isLink">
          {contact.links.map((link) => (
            <a href={link}>{link}</a>
          ))}
        </div>
        <div className="contact-isNotLink">
          {contact.notLinks.map((notLink) => (
            <p>{notLink}</p>
          ))}
        </div>
      </section>
      <section className="MAIN">
        <div className="professional-exp">
          {renderExperience("professional")}
        </div>
        <div className="project-exp">{renderExperience("project")}</div>
        <div className="education">
          <h3>EDUCATION</h3>
          <h2>
            {education.school}
            <span>{education.location}</span>
          </h2>
          <p>{education.subTitle}</p>
          <ul>
            {education.description.map((desc) => (
              <li>{desc}</li>
            ))}
          </ul>
        </div>
      </section>
      <section className="SPECS">
        <div className="technology">
          <ul>
            {technology.map((tech) => (
              <li>{tech}</li>
            ))}
          </ul>
        </div>
        <div className="honors">
          <h3>{honors.title}</h3>
          <p>{honors.description}</p>
        </div>
        <div className="languages">
          <ul>
            {languages.map((lang) => (
              <li>{lang}</li>
            ))}
          </ul>
        </div>
        <div className="interests">
          <ul>
            {interests.map((int) => (
              <li>{int} </li>
            ))}
          </ul>
        </div>
        <div className="hobbies">
          {hobbies.map((hobby) => (
            <li>{hobby} </li>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Resume;
