import Link from "next/link";

const Landing = () => (
  <div className="landing">
    <section id="intro">
      <h1>WOO YOUNG CHOI</h1>
      <p>
        <span>Designer</span> & <span>Front-end Developer</span> based in NY.
      </p>
    </section>
    <style jsx>
      {`
        .landing {
          align-items: center;
          display: flex;
          flex-direction: column;
          font-family: Futura;
          justify-content: center;
          min-height: 100vh;
          text-align: center;
        }
        h1 {
          display: block;
          font-family: Futura;
          font-size: 70px;
          font-weight: 600;
          text-shadow: 15px 15px #d9d9d9;
          transform: matrix(1, 0, 0, 1, 100, 0);
          flex-grow: 0
          flex-shrink: 0
          flex-basis: 40%;
          transition-duration: 0.4s;
          transition-property: transform;
          transition-timing-function: ease;
          -webkit-box-flex: 0;
        }
        p {
          font-family: Montserrat;
          font-size: 16px;
          font-weight: 100;
        }
        span {
          font-size: 19px;
          font-weight: 900;
        }
      `}
    </style>
  </div>
);

export default Landing;
