const Card = ({ id, quote = "QUOTE", description = "DESCRIPTION HERE" }) => {
  const renderDatabase = () => {
    const technology = Object.values(database).technology;
    return (
      <ul>
        {technology.map((val, i) => {
          return <li key={i}>{val}</li>;
        })}
        <style jsx>
          {`
            ul {
              display: flex;
              flex-direction: row;
              flex-wrap: wrap;
            }
            li {
              display: flexbox;
              padding: 8px;
              margin-right: 10px;
              margin-bottom: 10px;
              color: black;
              border: 3px solid #65b2d8;
              border-radius: 9px;
            }
          `}
        </style>
      </ul>
    );
  };

  return (
    <li className="wrapper">
      <div className="card">
        <PostImg id={id} />
      </div>
      <div className="card card--desc">
        <div className="text-wrapper">
          <div className="text">
            <PostLink id={id} />
            <h1>{quote}</h1>
            <p>{description}</p>
            <div className="tech-wrapper">{renderDatabase()}</div>
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .wrapper {
            display: flex;
            padding: 35px 0px;
          }
          .card {
            display: block;
            height: 350px;
            overflow: hidden;
            width: 60%;
            border: 1px solid gray;
          }
          .card--desc {
            border: none;
            text-align: center;
          }
          .text-wrapper {
            align-items: center;
          }
          .text {
            padding: 0px 60px;
            white-space: pre-line;
            height: 100%;
          }
          h1 {
            margin-bottom: 20px;
            margin-top: 10px;
          }
          .tech-wrapper {
            margin-top: 30px;
            text-align: center;
          }
        `}
      </style>
    </li>
  );
};

export default Card;
