import React from "react";

import Layout from "../components/Layout";
import resume from "../database/resume";

const Resume = () => {
  const values = Object.values(resume);
  console.log(values);

  const renderExperience = (expType) => (
    <>
      <h3>{expType.toUpperCase()} EXPERIENCE</h3>
      {values[3][expType].map((exp) => {
        const locationExist = exp.location ? (
          <>
            , <span>{exp.location}</span>
            <style jsx>
              {`
                span {
                  color: gray;
                }
              `}
            </style>
          </>
        ) : (
          ""
        );
        return (
          <div>
            <h2>
              {exp.title}
              {locationExist}
            </h2>
            <p>{exp.subTitle}</p>
            <ul>
              {exp.description.map((desc) => (
                <li>{desc}</li>
              ))}
            </ul>
            <style jsx>
              {`
                h2 {
                  font-size: 20px;
                }
                ul {
                  margin-left: 15px;
                }
                li {
                  padding: 5px;
                }
              `}
            </style>
          </div>
        );
      })}
    </>
  );

  return (
    <div className="background">
      <div className="page">
        <div className="bottom-layout">
          <section className="MAIN left-column">
            <div className="name">
              <h1>{values[0]}</h1>
            </div>
            <div className="professional-exp">
              {renderExperience("professional")}
            </div>
            <div className="project-exp">{renderExperience("project")}</div>
            <div className="education">
              <h3>EDUCATION</h3>
              <h2>
                {values[2].school + " "}
                <span>{values[2].location}</span>
              </h2>
              <p>{values[2].subTitle}</p>
              <ul>
                {values[2].description.map((desc) => (
                  <li>{desc}</li>
                ))}
              </ul>
            </div>
          </section>
          <section className=" SPECS right-column">
            <div className="contact">
              <div className="contact-isLink">
                <ul>
                  {values[1].links.map((link) => (
                    <li>
                      <a href={link}>{link}</a>
                    </li>
                  ))}
                  {values[1].notLinks.map((notLink) => (
                    <p>{notLink}</p>
                  ))}
                </ul>
              </div>
            </div>
            <div className="technology subsection">
              <h3>TECHNOLOGY</h3>
              <ul>
                {values[4].map((tech) => (
                  <li>{tech}</li>
                ))}
              </ul>
            </div>
            <div className="honors subsection">
              <h3>{values[5].title}</h3>
              <p>{values[5].description}</p>
            </div>
            <div className="languages subsection">
              <h3>LANGUAGES</h3>
              <ul>
                {values[6].map((lang) => (
                  <li>{lang}</li>
                ))}
              </ul>
            </div>
            <div className="interests subsection">
              <h3>INTERESTS</h3>
              <ul>
                {values[7].map((int) => (
                  <li>{int}</li>
                ))}
              </ul>
            </div>
            <div className="hobbies subsection">
              <h3>HOBBIES</h3>
              <ul>
                {values[8].map((hobby) => (
                  <li>{hobby}</li>
                ))}
              </ul>
            </div>
          </section>
        </div>
      </div>
      <style jsx>
        {`
          section {
            background-color: lightgray;
          }
          span {
            color: gray;
          }
          .background {
            display: flex;
            justify-content: center;
          }
          .page {
            display: flex;
            flex-direction: column;
            border: 1px solid black;
            padding: 50px;
            width: 960px;
            line-height: 1.8;
          }
          .name {
            display: block;
            font-size: 40px;
          }
          .bottom-layout {
            display: flex;
            height: 100%;
          }
          .MAIN {
            display: flex;
            flex-direction: column;
            height: 100%;
            flex: 2;
          }
          .SPECS {
            display: flex;
            flex-direction: column;
            background-color: pink;
            height: 100%;
            flex: 1;
          }
        `}
      </style>
    </div>
  );
};

export default Resume;
