import React, { useEffect } from "react";
import resume from "../database/resume";

const Resume = () => {
  const values = Object.values(resume);
  console.log(values);

  const renderExperience = (expType) => (
    <>
      <h3>{expType.toUpperCase()} EXPERIENCE</h3>
      {values[3][expType].map((exp) => {
        const locationExist = exp.location ? `, ${exp.location}` : "";
        return (
          <div>
            <h2>
              {exp.title}
              <span>{locationExist}</span>
            </h2>
            <p>{exp.subTitle}</p>
            <ul>
              {exp.description.map((desc) => (
                <li>{desc}</li>
              ))}
            </ul>
          </div>
        );
      })}
    </>
  );

  return (
    <div className="page-layout">
      <section className="HEAD">
        <div className="name">
          <h1>{values[0]}</h1>
        </div>
        <div className="contact-isLink">
          {values[1].links.map((link) => (
            <a href={link}>{link}</a>
          ))}
        </div>
        <div className="contact-isNotLink">
          {values[1].notLinks.map((notLink) => (
            <p>{notLink}</p>
          ))}
        </div>
      </section>
      <section className="MAIN">
        <div className="professional-exp">
          {renderExperience("professional")}
        </div>
        <div className="project-exp">{renderExperience("project")}</div>
        <div className="education">
          <h3>EDUCATION</h3>
          <h2>
            {values[2].school}
            <span>{values[2].location}</span>
          </h2>
          <p>{values[2].subTitle}</p>
          <ul>
            {values[2].description.map((desc) => (
              <li>{desc}</li>
            ))}
          </ul>
        </div>
      </section>
      <section className="SPECS">
        <div className="technology">
          <ul>
            {values[4].map((tech) => (
              <li>{tech}</li>
            ))}
          </ul>
        </div>
        <div className="honors">
          <h3>{values[5].title}</h3>
          <p>{values[5].description}</p>
        </div>
        <div className="languages">
          <ul>
            {values[6].map((lang) => (
              <li>{lang}</li>
            ))}
          </ul>
        </div>
        <div className="interests">
          <ul>
            {values[7].map((int) => (
              <li>{int}</li>
            ))}
          </ul>
        </div>
        <div className="hobbies">
          {values[8].map((hobby) => (
            <li>{hobby}</li>
          ))}
        </div>
      </section>
      <style jsx>
        {`
          .page-layout {
            display: flex;
            background-color: yellow;
            padding: 50px;
          }
          section {
            background-color: orange;
          }
        `}
      </style>
    </div>
  );
};

export default Resume;
