import Link from "next/link";
import Layout from "../components/Layout";
import { blogContent } from "../data_storage/database";

export default () => {
  // Add Post
  const addPosts = () => {
    const posts = blogContent.map(post => (
      <li className="post" key={post.id}>
        <PostLink post={post} />
        <style jsx>
          {`
            .post {
              display: flex-box;
              background-color: white;
              width: 800px;
              padding: 30px;
              margin-bottom: 10px;
              box-shadow: LightGray 0px 0px 10px 0px;
              width: 100%;
              border-radius: 10px;
            }
          `}
        </style>
      </li>
    ));
    return posts;
  };

  // PostLink Component
  const PostLink = ({ post }) => (
    <>
      <Link href="/p/[title]" as={`/p/${post.title}`}>
        <a>{post.title} </a>
      </Link>
      <style jsx>
        {`
          a {
            text-decoration: none;
            font-family: Futura;
            letter-spacing: 1px;
          }
          a:visited {
            color: darkblue;
          }
        `}
      </style>
    </>
  );

  return (
    <Layout>
      <div className="layout">
        <h1>B L O G</h1>
        <section>
          <ul>{addPosts()}</ul>
        </section>
      </div>
      <style jsx>
        {`
          h1 {
            background: black;
            color: white;
            font-size: 60px;
            font-weight: 400;
            font-family: Futura;
            margin-bottom: 20px;
          }
          .layout {
            padding-top: 50px;
            padding-left: 120px;
            padding-right: 120px;
            line-height: 1.5;
            background-color: rgb(239, 239, 239);
            height: 100vh;
          }
        `}
      </style>
    </Layout>
  );
};
